"use strict";document.querySelector(".button_toggle").addEventListener("click",()=>{document.querySelectorAll(".random__wrapper").forEach(e=>{e.classList.toggle("random__wrapper_active")})});document.querySelector(".random__button-1").addEventListener("click",()=>{let e=+document.querySelector(".random__range-start").value,t=+document.querySelector(".random__range-end").value,n=getRandomIntFromFields(e,t);document.querySelector(".random__result").textContent=n});document.querySelector(".random__button-2").addEventListener("click",()=>{document.querySelector(".random__result").textContent=";)"});function getRandomIntFromFields(e,t){e=parseInt(e);t=parseInt(t);return Math.floor(Math.random()*(t-e+1))+e}function getRandomIntFromTicks(){let e=[].slice.call(document.querySelectorAll(".random__number-tick")).filter(function(e){return e.checked}).length,t=0;console.log(e)}